export const rightToc = [{
  value: 'Tokens',
  id: 'tokens',
  children: [{
    value: 'Membership',
    id: 'membership',
    children: []
  }, {
    value: 'Rewards',
    id: 'rewards',
    children: []
  }]
}, {
  value: 'Capital Allocation',
  id: 'capital-allocation',
  children: []
